Create table books
(
	id integer not null primary key auto_increment,
	code integer, 
	new integer, 
	name varchar(500), 
	price decimal(8, 4), 
	izd varchar(200), 
	npages integer, 
	format varchar(300), 
	dateizd varchar(100), 
	pressrun integer, 
	themes varchar(120), 
	category varchar(120) 
)DEFAULT CHARSET=utf8;


